 const axios = require('axios')

 class KodePosScraper {
 constructor() {
 this.baseUrl = 'https://kodepos.co.id/data'
 this.endpoints = {
 provinsi: '/provinsi.json',
 kota: '/kota.json',
 kecamatan: '/kecamatan.json',
 kelurahan: '/kelurahan.json'
 }
 }

 async fetchData(endpoint) {
 try {
 const res = await axios.get(`${this.baseUrl}${endpoint}`, {
 timeout: 15000,
 headers: { 'User-Agent': 'Mozilla/5.0' }
 })
 return Array.isArray(res.data) ? res.data : []
 } catch {
 return []
 }
 }

 async search(query) {
 if (!query || typeof query !== 'string' || query.trim().length < 3) {
 return { message: 'Query minimal 3 karakter', data: [] }
 }

 const keyword = query.toLowerCase().trim()

 const [provinsiData, kotaData, kecamatanData, kelurahanData] = await Promise.all([
 this.fetchData(this.endpoints.provinsi),
 this.fetchData(this.endpoints.kota),
 this.fetchData(this.endpoints.kecamatan),
 this.fetchData(this.endpoints.kelurahan)
 ])

 const matches = kelurahanData.filter((item) => {
 const nama = String(item.nama || '').toLowerCase()
 const kodePos = String(item.kode_pos ?? '')
 const kec = String(item.kecamatan_nama || '').toLowerCase()
 const kota = String(item.kota_nama || '').toLowerCase()
 const prov = String(item.provinsi_nama || '').toLowerCase()

 return (
 nama.includes(keyword) ||
 kodePos.includes(keyword) ||
 kec.includes(keyword) ||
 kota.includes(keyword) ||
 prov.includes(keyword)
 )
 })

 if (matches.length === 0) {
 return { message: 'Data tidak ditemukan', data: [] }
 }

 const formatted = matches.map((kel) => {
 const kec = kecamatanData.find(
 (k) => k.nama === kel.kecamatan_nama && k.kota_nama === kel.kota_nama
 )
 const kot = kotaData.find(
 (c) => c.nama === kel.kota_nama && c.provinsi_nama === kel.provinsi_nama
 )
 const prov = provinsiData.find((p) => p.nama === kel.provinsi_nama)

 return {
 kodePos: kel.kode_pos,
 kelurahan: {
 nama: kel.nama,
 kemendagri: kel.kode_kemendagri,
 lat: kel.lat,
 lng: kel.lng,
 elevasi: kel.elevasi
 },
 kecamatan: kec
 ? { nama: kec.nama, kemendagri: kec.kode_kemendagri, zona: kec.zona_waktu }
 : null,
 kota: kot
 ? { nama: kot.nama, kemendagri: kot.kode_kemendagri, lat: kot.lat, lng: kot.lng }
 : null,
 provinsi: prov
 ? { nama: prov.nama, kemendagri: prov.kode_kemendagri, zona: prov.zona_waktu }
 : null
 }
 })

 return { total: formatted.length, query, results: formatted }
 }
 }

